import java.util.*;
import java.io.*;
import java.math.BigInteger;

public class HexToDec {
	public static void main(String args[]){  
		
		String hex = "";
		System.out.println("Enter the value in Hex: ");
		BigInteger toDec=new BigInteger(hex,16);
		String s=toDec.toString(10);
		System.out.println("The value in Dec is: "+ s);
	}
}